import React from 'react';
import {Button, Form, FormGroup, Label, Input} from 'reactstrap'




class FormA extends React.Component{
    constructor(props){
        super(props)
        this.state = {

            object :{
            name :'',
            cost :'',
            weight: ''},

            objectList: []
        }
    }



    sumbitFormA = (event) => {



        transform = async() => {

            await this.setState({

                object:{
                    name: event.target.value,
                    cost: event.target.value,
                    weight: event.target.value
                },

                objectList:[...this.state.objectList, this.state.object]
            })

        }

        alert(`Name: ${this.object.name} Value: ${FormA.value.value} Weight: ${FormA.weight.value} List of Obj: ${this.state.objectList}`)



        event.preventDefaul()

    }

    render(){
    return (
        <>

        <Form className='m-3'>
            <FormGroup>
                <Label htmlFor="bagSize">BAG SIZE</Label>
                <Input type="text" id='bagSize' name='bagSize' placeholder='Bag Size' required/>
            </FormGroup>
            <Button type='submit' value='submit' color='primary'>Submit</Button>
        </Form>

        <Form onSubmit={this.sumbitFormA} className='m-3'>

            <FormGroup>
                <Label htmlFor="name">Name</Label>
                <Input type="text" id='name' name='name' placeholder='Name of the object' required/>
            </FormGroup>

            <FormGroup>
                <Label htmlFor="cost">Cost</Label>
                <Input type="text" id='cost' name='cost' placeholder='Cost of the object' required/>
            </FormGroup>

            <FormGroup>
                <Label htmlFor="weight">Weight</Label>
                <Input type="text" id='weight' name='weight' placeholder='Weight of the object' required/>
            </FormGroup>

                <Button type='submit' value='submit' color='primary'>Add</Button>
        </Form>

    </>
    )}
}

export default FormA
