import React from 'react';
import './ListOfCards.style.css';
import {CardIndividual} from '../card/CardIndividual.component';


class ListOfCards extends React.Component {
    constructor(){
        super();
        this.state ={
            algorithmList:[],
            searchField:''
        }
    }

    // async way 

    componentDidMount = async() => {
        try {
            const getDataB = await fetch('http://localhost:3000/algorithms')
            const dataToJ = await getDataB.json()
            this.setState({algorithmList: dataToJ})
        }
        catch(err){
            console.log('not possible now');
        }
    }

    // promises

    // componentDidMount() => {
    //     const getDataB = await fetch('')
    //     .then(response => response.json())
    //     .then(alg => this.setState({algorithmList:alg}))
    // .catch((e)=>(console.log('not possible now'))
    // }

    render(){
        return(
            <>
                <div className="container">
                    <div className="col">
                        <div className="row">
                            <h1>Algorithm List</h1>

                            <input type='search' placeholder='search algorithm' onChange={ e => this.setState({searchField:e.target.value},()=> console.log(this.state)
                            )} />

                        </div>
                    </div>
                </div>
                <div className="container">
                    <div className="col">
                        <div className="row">
                            <div className='card-list'>
                                {this.state.algorithmList.map( (x,index) => 
                                    <CardIndividual
                                    key={index} index={index} x={x} />)
                                }
                            </div>
                        </div>
                    </div>
                </div>
            </>
        )
    }

}

export default ListOfCards
