import { createGlobalStyle } from 'styled-components';

export const GlobalStyle = createGlobalStyle`

    body{
        padding: 20px 60px;
    }

    .font{
        font-family: 'Barlow Condensed', sans-serif;
        font-size: 2rem;
        color: black;
    }

    .font a{
        text-decoration: none;
        color:black;
    }

    .font a:hover{
        text-decoration: none;
        color:black;
    }

    .font:hover{
        text-decoration: none;
    }

    *{
        box-sizing: border-box;
    }

`
