import styled from 'styled-components';

export const MenuItemContainer = styled.div`

    min-width: 30%;
    height: 240px;
    flex: 1 1 auto;
    display: flex;
    align-items: center;
    justify-content: center;
    border: 1px solid black;
    margin: 0 7.5px 15px;
    overflow: hidden;

    &:hover {
      cursor: pointer;

      & .backgrounImage {
        transform: scale(1.1);
        transition: transform 6s cubic-bezier(0.25, 0.45, 0.45, 0.95);
      }

      & .content{
        opacity: 0.9;
      }
    }


    &.large{
      height: 380px
    }

    &:first-child {
      margin-right: 7.5px;
    }

    &:last-child {
      margin-left: 7.5px;
    }

    .backgrounImage {
      width: 100%;
      height: 100%;
      background-size: cover;
      background-position: center;
    }

    @media screen and (max-width:800px)

    // .backgrounImage:hover {
    //   width: 120%;
    //   height: 120%;
    // }

    .content {
      height: 90px;
      padding: 0 15px;
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      border: 1px solid black;

      background-color: white;
      opacity: 0.7;

      position: absolute;




      .title {
        font-weight: bold;
        margin-top: 10px;
        font-size: 22px;
        color: #4a4a4a;
        align-items: center;

      }

      .subtitle {
        font-weight: lighter;
        font-size: 16px;
        margin-left: 25%;
      }
    }

`
