import styled from 'styled-components'
import {Container,Col,Row} from 'reactstrap'


export const CardList = styled(Col)`
    width: 80vw;
    margin: auto;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr 1fr;
    grid-gap: 5px;
`
