import React from 'react';

import './formInput.styles.scss'

const FormInput = ({ handleChange,value, label , ...restOfprops}) => {
    return(
        <div className='group'>
            <input className='form-input' 
            onChange={handleChange} {...restOfprops} />
                {label?
                (<label className={`${
                    value.length ? 'shrink' : ''} 
                form-input-label`}>
                    {label}
                </label>
                ):null
            }
        </div>
    )

}

export default FormInput
