import styled from 'styled-components'

import {NavigStyled} from 'react-router-dom'


export const NavItems = styled(NavLink)`
    display: flex;
    flex-direction: row;
    align-items: center;

    &:hover{
        transform: scale(1.25);
        color: black;
        text-decoration: none;
    }

    &:visited{
    color: black;
    text-decoration: none;
    }

`
